import './App.css';
import {useRive, useStateMachineInput} from 'rive-react';

function App() {

  const STATE_MACHINE_NAME = "state";
  const {rive, RiveComponent} = useRive({
    src: "./quiz.riv",
    autoplay: true,
    stateMachines: STATE_MACHINE_NAME
  });


  return (
    <div className="App">
     <div >
        <RiveComponent style={{width:'500px', height:'500px'}} src="quiz.riv"/>
      </div>
    </div>
  );
}

export default App;
